    def animate_frame_border_color(self):
        if self.pushButton.isChecked():
            # Change the color to green (15, 255, 90) with full opacity (255)
            self.shadow_effect.setColor(QColor(15, 255, 90, 255))
            # Apply the effect to the button
            self.pushButton.setGraphicsEffect(self.shadow_effect)
            # self.frame_2.setStyleSheet('''
            # #frame_2 {
            # background-color: #222831;
            # border: 7px solid green;
            # border-radius: 40px;
            # }'''
            # )
        else:
            # Change the color to red (255, 0, 0) with full opacity (255)
            self.shadow_effect.setColor(QColor(255, 0, 0, 255))
            # Apply the effect to the button
            self.pushButton.setGraphicsEffect(self.shadow_effect)
