    def add_device(self):
        device_id = self.plainTextEdit_2.toPlainText()
        device_type = self.comboBox_2.currentText()
        device_protocol = self.comboBox_3.currentText()
        device_ip = self.plainTextEdit_3.toPlainText()
        #add these to json file 
        device_entry = generate_device_entry(device_id,device_type,device_protocol,device_ip)
        e = add_device_entry(device_entry,"./settings_iot_data/iot_device.json")
        if e != 0:
            self.show_dialog_box(e)

    def set_voice(self):
        ...

    def show_dialog_box(self,e: str):
        msg = QtWidgets.QMessageBox()
        msg.setIcon(QtWidgets.QMessageBox.Information)
        msg.setWindowTitle("ERROR OCCURRED")
        msg.setText(e)
        msg.setStandardButtons(QtWidgets.QMessageBox.Ok)
        res: int = msg.exec()
        if res == QtWidgets.QMessageBox.Ok:
            self.plainTextEdit_2.clear()
            self.plainTextEdit_3.clear()
            self.plainTextEdit_2.setFocus()

    def discard_fun(self):
        self.plainTextEdit.clear()
        self.plainTextEdit_2.clear()
        self.plainTextEdit_3.clear()
        


